const STATE = require('STATE')
const statedb = STATE(__filename)
const { sdb, get } = statedb(fallback_module)

const transaction_row = require('transaction_row')

module.exports = transaction_history

async function transaction_history (opts = {}) {
  const { id, sdb } = await get(opts.sid)
  const { drive } = sdb

  const on = {
    style: inject,
    data: ondata
  }

  const el = document.createElement('div')
  const shadow = el.attachShadow({ mode: 'closed' })

  shadow.innerHTML = `
    <div class="component-label">Transaction History</div>
    <div class="transaction-history-container"></div>
    <style></style>
  `

  const style = shadow.querySelector('style')
  const containerEl = shadow.querySelector('.transaction-history-container')

  const subs = await sdb.watch(onbatch)

  const grouped = {}

  subs.forEach(sub => {
    console.log('Transaction date:', sub.date)
    const date = (sub.date || 'Unknown').trim() // trim extra spaces
    if (!grouped[date]) grouped[date] = []
    grouped[date].push(sub)
  })
  
  for (const date in grouped) {
    const dateEl = document.createElement('div')
    dateEl.className = 'transaction-date'
    dateEl.innerHTML = `<span>${date}</span>`
    containerEl.appendChild(dateEl)

    for (const sub of grouped[date]) {
      const row = await transaction_row(sub)
      containerEl.appendChild(row)
    }
  }


  return el

  function fail(data, type) {
    throw new Error('invalid message', { cause: { data, type } })
  }

  async function onbatch (batch) {
    for (const { type, paths } of batch){
      const data = await Promise.all(paths.map(path => drive.get(path).then(file => file.raw)))
      const func = on[type] || fail
      await func(data, type)
    }
  }

  function inject (data) {
    style.textContent = data[0]
  }

  async function ondata(data) {
    // await renderValues(data[0]?.value || {})
  }

  // async function renderValues(dataList) {
  //   const container = document.createElement('div')

  //   if (!Array.isArray(dataList)) dataList = [dataList]

  //   const grouped = {}
  //   for (const item of dataList) {
  //     const { date = "Unknown" } = item
  //     if (!grouped[date]) grouped[date] = []
  //     grouped[date].push(item)
  //   }

  //   for (const date in grouped) {
  //     const dateEl = document.createElement('div')
  //     dateEl.className = 'transaction-date'
  //     dateEl.innerHTML = `<span>${date}</span>`
  //     container.appendChild(dateEl)

  //     for (const tx of grouped[date]) {
  //       const { tid, ttime, tamount, avatar } = tx
        
  //       const row = await transaction_row({ tid, ttime, tamount, avatar })
  //       container.appendChild(row)
  //     }
  //   }

  //   const containerEl = shadow.querySelector('.transaction-history-container')
  //   containerEl.innerHTML = `<div class="transaction-history-header">Transaction history</div>`
  //   containerEl.appendChild(container)
  // }
}

function fallback_module () {
  return {
    api,
    _: {
      'transaction_row':{
        $: ''
      }
    } 
  }
  function api(opts){
    const transaction_row = {
      mapping: {
        style: 'style',
        data: 'data'
      }
    }
    opts.value.forEach((transaction, index) => {
      transaction_row[index] = transaction
    })
    return {
      drive: {
        'style/': {
          'transaction_history.css':{
            '$ref': 'transaction_history.css'
          }
        },
        'data/': {
          'opts.json':{
            raw: opts
          }
        }
      },
      _:{
        transaction_row
      }
    }
  }
}
